import styled from 'styled-components'

export const BgContainer = styled.div`
  height: 100vh;
  display: flex;
  flex-direction: row;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background-color: ${props => (props.lightTheme ? '#f9f9f9' : '#0f0f0f')};
`
