import {Component} from 'react'
import Cookies from 'js-cookie'
import Loader from 'react-loader-spinner'
import {AiOutlineSearch} from 'react-icons/ai'
import Header from '../Header'
import Navbar from '../Navbar'
import ThemeContext from '../../context/ThemeContext'
import {
  LoaderContainer,
  SearchContainer,
  SearchIp,
  SearchButton,
  Cont,
} from './styledComponents'

class Home extends Component {
  state = {status: 'loading', searchIp: '', showPopup: true, arrayList: []}

  componentDidMount() {
    this.getVideos()
  }

  getVideos = async (searchVal = '') => {
    const jwtToken = Cookies.get('jwt_token')
    const url = `https://apis.ccbp.in/videos/all?search=${searchVal}`
    const options = {
      headers: {
        Authorization: `Bearer ${jwtToken}`,
      },
      method: 'GET',
    }
    const response = await fetch(url, options)
    const data = await response.json()
    if (response.ok) {
      this.setState({status: 'success', arrayList: data}, this.renderHome)
    }
  }

  renderLoader = () => (
    <LoaderContainer>
      <Loader type="Puff" color="#475569" height="60" width="60" />
    </LoaderContainer>
  )

  onChange = event => {
    this.setState({searchIp: event.target.value})
  }

  onSearch = () => {
    const {searchIp} = this.state
    this.getVideos(searchIp)
  }

  onKey = event => {
    if (event.key.toLowerCase() === 'enter') {
      this.onSearch()
    }
  }

  renderSearchBar = () => (
    <ThemeContext.Consumer>
      {value => {
        const {lightTheme} = value
        const {searchIp} = this.state
        return (
          <SearchContainer>
            <SearchIp
              placeholder="Search Channel"
              type="search"
              value={searchIp}
              onChange={this.onChange}
              onKeyDown={this.onKey}
            />
            <SearchButton onClick={this.onSearch}>
              <AiOutlineSearch size={20} />
            </SearchButton>
          </SearchContainer>
        )
      }}
    </ThemeContext.Consumer>
  )

  renderVideos = () => (
    <ThemeContext.Consumer>
      {value => {
        const {lightTheme} = value
        return <>{this.renderSearchBar()}</>
      }}
    </ThemeContext.Consumer>
  )

  renderHome = () => {
    const {status} = this.state
    switch (status) {
      case 'loading':
        return this.renderLoader()
      case 'success':
        return this.renderVideos()
      default:
        return null
    }
  }

  render() {
    return (
      <ThemeContext.Consumer>
        {value => {
          const {lightTheme} = value
          return (
            <>
              <Header lightTheme={lightTheme} />
              <Cont>
                <Navbar />
                {this.renderHome()}
              </Cont>
            </>
          )
        }}
      </ThemeContext.Consumer>
    )
  }
}

export default Home
