import {Component} from 'react'
import Cookies from 'js-cookie'
import ReactPlayer from 'react-player'

import {AiOutlineLike, AiOutlineDislike} from 'react-icons/ai'
import {MdPlaylistAdd} from 'react-icons/md'
import Header from '../Header'
import Navbar from '../Navbar'

import {
  VideoFrameContainer,
  VideoContainer,
  ParaEl,
  AttributesContainer,
  ChannelContainer,
  ImageEl,
  ContentContainer,
  IconParas,
  Cont,
} from './styledComponents'

import ThemeContext from '../../context/ThemeContext'

import './index.css'

class VideoItemDetails extends Component {
  state = {
    videoDetails: {
      id: 'ad9822d2-5763-41d9-adaf-baf9da3fd490',
      title: 'iB Hubs Announcement Event',
      video_url: 'https://www.youtube.com/watch?v=pT2ojWWjum8',
      thumbnail_url:
        'https://assets.ccbp.in/frontend/react-js/nxt-watch/ibhubs-img.png',
      channel: {
        name: 'iB Hubs',
        profile_image_url:
          'https://assets.ccbp.in/frontend/react-js/nxt-watch/ib-hubs-img.png',
        subscriber_count: '1M',
      },
      view_count: '26K',
      published_at: 'Nov 29, 2016',
      description:
        'iB Hubs grandly celebrated its Announcement Event in November 13, 2016, in the presence of many eminent personalities from the Government, Industry, and Academia with Shri Amitabh Kant, CEO, NITI Aayog as the Chief Guest.',
    },
    liked: false,
    disliked: false,
    saved: false,
  }

  componentDidMount() {
    this.getData()
  }

  componentWillUnmount() {
    this.mounted = false
  }

  getData = async () => {
    this.mounted = true
    const {match} = this.props
    const {params} = match
    const {id} = params
    const jwtToken = Cookies.get('jwt_token')
    const url = `https://apis.ccbp.in/videos/${id}`
    const options = {
      headers: {
        Authorization: `Bearer ${jwtToken}`,
      },
      method: 'GET',
    }
    const response = await fetch(url, options)
  }

  isDisliked = () => {
    const {liked, disliked} = this.state
    if (liked) {
      this.setState({liked: false})
    }
    if (disliked) {
      this.setState({disliked: false})
    } else {
      this.setState({disliked: true})
    }
  }

  isLiked = () => {
    const {liked, disliked} = this.state
    if (disliked) {
      this.setState({disliked: false})
    }
    if (liked) {
      this.setState({liked: false})
    } else {
      this.setState({liked: true})
    }
  }

  isSaved = async () => {
    const {saved} = this.state
    if (saved) {
      await this.setState({saved: false})
    } else {
      await this.setState({saved: true})
    }
  }

  renderVideo = () => {
    const {videoDetails, liked, disliked, saved} = this.state
    const {channel} = videoDetails
    return (
      <ThemeContext.Consumer>
        {values => {
          const {lightTheme, addSavedVideos} = values
          const bgColor = lightTheme ? '#ffffff' : '#000000'
          const color = lightTheme ? '#000000' : '#ffffff'

          const onSave = () => {
            this.isSaved()
            addSavedVideos(videoDetails)
          }

          return (
            <VideoContainer bgColor={bgColor} color={color}>
              <VideoFrameContainer>
                <ReactPlayer
                  url={videoDetails.video_url}
                  controls
                  className="react-player"
                />
                <ParaEl>
                  <b>{videoDetails.title}</b>
                </ParaEl>
              </VideoFrameContainer>
              <AttributesContainer>
                <ParaEl>
                  {videoDetails.view_count} views . {videoDetails.published_at}
                </ParaEl>
                <ChannelContainer color={color}>
                  <IconParas
                    onClick={this.isLiked}
                    iconColor={liked ? '#3b82f6' : color}
                  >
                    <AiOutlineLike size={20} /> Like
                  </IconParas>
                  <IconParas
                    onClick={this.isDisliked}
                    iconColor={disliked ? '#3b82f6' : color}
                  >
                    <AiOutlineDislike size={20} /> Dislike
                  </IconParas>
                  <IconParas
                    onClick={onSave}
                    iconColor={saved ? '#3b82f6' : color}
                  >
                    <MdPlaylistAdd size={20} /> {saved ? 'Saved' : 'Save'}
                  </IconParas>
                </ChannelContainer>
              </AttributesContainer>
              <ChannelContainer>
                <ImageEl src={channel.profile_image_url} />
                <ContentContainer>
                  <ParaEl>
                    <b>{channel.name}</b>
                  </ParaEl>
                  <ParaEl>{channel.subscriber_count}</ParaEl>
                </ContentContainer>
              </ChannelContainer>
              <ParaEl padding="30px">{videoDetails.description}</ParaEl>
            </VideoContainer>
          )
        }}
      </ThemeContext.Consumer>
    )
  }

  render() {
    return (
      <ThemeContext.Consumer>
        {value => {
          const {lightTheme} = value
          return (
            <>
              <Header lightTheme={lightTheme} />
              <Cont>
                <Navbar />
                {this.renderVideo()}
              </Cont>
            </>
          )
        }}
      </ThemeContext.Consumer>
    )
  }
}

export default VideoItemDetails
