import {Link, withRouter} from 'react-router-dom'
import {Component} from 'react'
import Cookies from 'js-cookie'
import {FiSun, FiLogOut} from 'react-icons/fi'
import {IoMoon} from 'react-icons/io5'
import {GiHamburgerMenu} from 'react-icons/gi'
import ThemeContext from '../../context/ThemeContext'

import {
  NavBar,
  WebsiteLogo,
  IconsContainer,
  ChangeThemeButton,
  LabelElement,
  SelectElement,
  SelectItem,
} from './styledComponents'
import './index.css'

class Header extends Component {
  state = {
    showOptions: false,
  }

  componentDidMount() {
    this.setState({showOptions: false})
  }

  onClickLogout = () => {
    Cookies.remove('jwt_token')
    const {history} = this.props
    history.replace('/login')
  }

  onClickShowOptions = () => {
    this.setState(prevState => ({showOptions: !prevState.showOptions}))
  }

  render() {
    const {showOptions} = this.state
    return (
      <ThemeContext.Consumer>
        {value => {
          const {lightTheme, changeTheme} = value
          return (
            <>
              <NavBar lightTheme={lightTheme}>
                <WebsiteLogo
                  src={
                    lightTheme
                      ? 'https://assets.ccbp.in/frontend/react-js/nxt-watch-logo-light-theme-img.png'
                      : 'https://assets.ccbp.in/frontend/react-js/nxt-watch-logo-dark-theme-img.png'
                  }
                  alt="website logo"
                />
                <IconsContainer>
                  <ChangeThemeButton
                    type="button"
                    lightTheme={lightTheme}
                    onClick={changeTheme}
                  >
                    {lightTheme ? <IoMoon /> : <FiSun />}
                  </ChangeThemeButton>
                  <ChangeThemeButton
                    type="button"
                    lightTheme={lightTheme}
                    onClick={this.onClickShowOptions}
                  >
                    <GiHamburgerMenu />
                  </ChangeThemeButton>
                  <ChangeThemeButton
                    type="button"
                    lightTheme={lightTheme}
                    onClick={this.onClickLogout}
                  >
                    <FiLogOut />
                  </ChangeThemeButton>
                </IconsContainer>
              </NavBar>
              {showOptions && (
                <LabelElement lightTheme={lightTheme}>
                  <Link to="/" className="linkItem">
                    <SelectElement lightTheme={lightTheme}>
                      <SelectItem lightTheme={lightTheme}>Home</SelectItem>
                    </SelectElement>
                  </Link>
                  <Link to="/trending">
                    <SelectElement lightTheme={lightTheme}>
                      <SelectItem lightTheme={lightTheme}>Trending</SelectItem>
                    </SelectElement>
                  </Link>
                  <Link to="/gaming">
                    <SelectElement lightTheme={lightTheme}>
                      <SelectItem lightTheme={lightTheme}>Gaming</SelectItem>
                    </SelectElement>
                  </Link>
                  <Link to="/saved-videos">
                    <SelectElement lightTheme={lightTheme}>
                      <SelectItem lightTheme={lightTheme}>
                        Saved Videos
                      </SelectItem>
                    </SelectElement>
                  </Link>
                </LabelElement>
              )}
            </>
          )
        }}
      </ThemeContext.Consumer>
    )
  }
}

export default withRouter(Header)
