import {Component} from 'react'
import Cookies from 'js-cookie'
import Loader from 'react-loader-spinner'
import {AiOutlineSearch, AiOutlineClose} from 'react-icons/ai'
import Header from '../Header'
import Navbar from '../Navbar'
import ThemeContext from '../../context/ThemeContext'
import {
  LoaderContainer,
  SearchContainer,
  SearchIp,
  SearchButton,
  Cont,
  VideosContainer,
  PopupContainer,
  PopupFirstContainer,
  PopupContainerImage,
  PopupContainerDescription,
  PopupContainerButton,
  PopupContainerClose,
  FailureContainer,
  FailureImage,
  FailureHeading,
  FailureDescription,
  FailureRetryButton,
} from './styledComponents'

class Home extends Component {
  state = {status: 'loading', searchIp: '', showPopup: true, arrayList: []}

  componentDidMount() {
    this.getVideos()
  }

  getVideos = async (searchVal = '') => {
    const jwtToken = Cookies.get('jwt_token')
    const url = `https://apis.ccbp.in/videos/all?search=${searchVal}`
    const options = {
      headers: {
        Authorization: `Bearer ${jwtToken}`,
      },
      method: 'GET',
    }
    const response = await fetch(url, options)
    const data = await response.json()
    if (response.ok) {
      this.setState(
        {status: 'success', arrayList: data, searchIp: ''},
        this.renderHome,
      )
    } else {
      this.setState({status: 'failure', searchIp: ''}, this.renderHome())
    }
  }

  renderLoader = () => (
    <ThemeContext.Consumer>
      {value => {
        const {lightTheme} = value
        return (
          <LoaderContainer lightTheme={lightTheme}>
            <Loader type="Puff" color="#475569" height="60" width="60" />
          </LoaderContainer>
        )
      }}
    </ThemeContext.Consumer>
  )

  onChange = event => {
    this.setState({searchIp: event.target.value})
  }

  onSearch = () => {
    const {searchIp} = this.state
    this.getVideos(searchIp)
  }

  onKey = event => {
    if (event.key.toLowerCase() === 'enter') {
      this.onSearch()
    }
  }

  onClickClose = () => {
    this.setState({showPopup: false})
  }

  renderPopup = () => (
    <PopupContainer>
      <PopupFirstContainer>
        <PopupContainerImage
          src="https://assets.ccbp.in/frontend/react-js/nxt-watch-logo-light-theme-img.png"
          alt="website logo"
        />
        <PopupContainerDescription>
          Buy Nxt Watch Premium prepaid plans with UPI
        </PopupContainerDescription>
        <PopupContainerButton type="button">GET IT NOW</PopupContainerButton>
      </PopupFirstContainer>
      <PopupContainerClose type="button" onClick={this.onClickClose}>
        <AiOutlineClose />
      </PopupContainerClose>
    </PopupContainer>
  )

  renderSearchBar = () => (
    <ThemeContext.Consumer>
      {value => {
        const {lightTheme} = value
        const {searchIp} = this.state
        return (
          <SearchContainer lightTheme={lightTheme}>
            <SearchIp
              placeholder="Search Channel"
              type="search"
              value={searchIp}
              onChange={this.onChange}
              onKeyDown={this.onKey}
              lightTheme={lightTheme}
            />
            <SearchButton onClick={this.onSearch} lightTheme={lightTheme}>
              <AiOutlineSearch
                size={20}
                color={lightTheme ? '#64748b' : '#606060'}
              />
            </SearchButton>
          </SearchContainer>
        )
      }}
    </ThemeContext.Consumer>
  )

  renderNoSearchResults = () => (
    <ThemeContext.Consumer>
      {value => {
        const {lightTheme} = value
        return (
          <FailureContainer>
            <FailureImage
              src="https://assets.ccbp.in/frontend/react-js/nxt-watch-no-search-results-img.png"
              alt="no videos"
            />
            <FailureHeading lightTheme={lightTheme}>
              No Search Results Found
            </FailureHeading>
            <FailureDescription lightTheme={lightTheme}>
              Try different key words or remove search filters
            </FailureDescription>
            <FailureRetryButton type="button" onClick={this.onClickRetry}>
              Retry
            </FailureRetryButton>
          </FailureContainer>
        )
      }}
    </ThemeContext.Consumer>
  )

  renderFailureView = () => (
    <ThemeContext.Consumer>
      {value => {
        const {lightTheme} = value
        const {showPopup} = this.state
        return (
          <VideosContainer lightTheme={lightTheme}>
            {showPopup && this.renderPopup()}
            {this.renderSearchBar()}
            <FailureContainer>
              <FailureImage
                src={
                  lightTheme
                    ? 'https://assets.ccbp.in/frontend/react-js/nxt-watch-failure-view-light-theme-img.png'
                    : 'https://assets.ccbp.in/frontend/react-js/nxt-watch-failure-view-dark-theme-img.png'
                }
                alt="failure view"
              />
              <FailureHeading lightTheme={lightTheme}>
                Oops! Something Went Wrong
              </FailureHeading>
              <FailureDescription lightTheme={lightTheme}>
                We are having some trouble to complete your request. <br />
                Please try again.
              </FailureDescription>
              <FailureRetryButton type="button" onClick={this.onClickRetry}>
                Retry
              </FailureRetryButton>
            </FailureContainer>
          </VideosContainer>
        )
      }}
    </ThemeContext.Consumer>
  )

  renderVideos = () => {
    const {showPopup, arrayList} = this.state
    return (
      <ThemeContext.Consumer>
        {value => {
          const {lightTheme} = value
          return (
            <VideosContainer lightTheme={lightTheme}>
              {showPopup && this.renderPopup()}
              {this.renderSearchBar()}
              {arrayList.length !== 0
                ? this.renderNoSearchResults()
                : this.renderEachVideo()}
            </VideosContainer>
          )
        }}
      </ThemeContext.Consumer>
    )
  }

  renderHome = () => {
    const {status} = this.state
    switch (status) {
      case 'loading':
        return this.renderLoader()
      case 'success':
        return this.renderVideos()
      case 'failure':
        return this.renderFailureView()
      default:
        return null
    }
  }

  render() {
    return (
      <ThemeContext.Consumer>
        {value => {
          const {lightTheme} = value
          return (
            <>
              <Header lightTheme={lightTheme} />
              <Cont>
                <Navbar />
                {this.renderHome()}
              </Cont>
            </>
          )
        }}
      </ThemeContext.Consumer>
    )
  }
}

export default Home
