import styled from 'styled-components'

export const NavBar = styled.nav`
  background-color: ${props => (props.lightTheme ? '#f9f9f9' : ' #0f0f0f')};
  padding: 10px;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
`

export const WebsiteLogo = styled.img`
  width: 100px;
`

export const IconsContainer = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  @media screen and (min-width: 768px) {
    display: none;
  }
`

export const LargeScreenIconsContainer = styled.div`
  display: none;
  @media screen and (min-width: 768px) {
    display: flex;
    flex-direction: row;
    align-items: flex-start;
  }
`

export const ChangeThemeButton = styled.button`
  background-color: transparent;
  border: 0px;
  cursor: pointer;
  color: ${props => (props.lightTheme ? '#0f0f0f' : ' #f9f9f9')};
  font-size: 20px;
  margin-right: 10px;
`

export const LabelElement = styled.ul`
  color: ${props => (props.lightTheme ? '#0f0f0f' : ' #f9f9f9')};
  font-size: 20px;
  margin-right: 10px;
  background-color: ${props => (props.lightTheme ? '#f9f9f9' : ' #0f0f0f')};
  list-style: none;
  margin: 0px;
  padding: 0px;
`

export const SelectElement = styled.li`
  display: flex;
  flex-direction: row;
  list-style: none;
  border: 0px;
  border-bottom-width: 1px;
  border-style: solid;
  border-color: ${props => (props.lightTheme ? '#0f0f0f' : ' #f9f9f9')};
`

export const SelectItem = styled.p`
  margin-left: 10px;
  color: ${props => (props.lightTheme ? '#0f0f0f' : ' #f9f9f9')};
`

export const ProfileImage = styled.img`
  width: 20px;
`
