import styled from 'styled-components'

export const LoaderContainer = styled.div`
  background-color: ${props => (props.lightTheme ? '#f8fafc' : '  #000000')};
  height: 90vh;
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
`
