import ThemeContext from '../../context/ThemeContext'
import {
  ListImage,
  ListContainer,
  Container,
  ChannelImage,
  ChannelName,
  Title,
} from './styledComponents'

const VideoItem = props => {
  const {details} = props
  const {channel} = details
  return (
    <ThemeContext.Consumer>
      {value => {
        const {lightTheme} = value
        return (
          <ListContainer>
            <ListImage src={details.thumbnail_url} alt="thumbnail" />
            <Container>
              <ChannelImage src={channel.profile_image_url} />
              <div>
                <Title lightTheme={lightTheme}>{details.title}</Title>
                <ChannelName lightTheme={lightTheme}>
                  {channel.name}
                </ChannelName>
              </div>
            </Container>
          </ListContainer>
        )
      }}
    </ThemeContext.Consumer>
  )
}

export default VideoItem
