import {Component} from 'react'
import Loader from 'react-loader-spinner'
import Header from '../Header'
import ThemeContext from '../../context/ThemeContext'
import Cookies from 'js-cookie'
import {LoaderContainer} from './styledComponents'

class Trending extends Component {
  state = {status: 'loading', arrayList: []}

  componentDidMount() {
    this.getVideos()
  }

  getVideos = async () => {
    const jwtToken = Cookies.get('jwt_token')
    const url = `https://apis.ccbp.in/videos/all?search=${searchVal}`
    const options = {
      headers: {
        Authorization: `Bearer ${jwtToken}`,
      },
      method: 'GET',
    }
    const response = await fetch(url, options)
    const data = await response.json()
    if (response.ok) {
      this.setState({status: 'success', arrayList: data}, this.renderHome)
    } else {
      this.setState({status: 'failure'}, this.renderHome())
    }
  }

  renderLoader = () => (
    <ThemeContext.Consumer>
      {value => {
        const {lightTheme} = value
        return (
          <LoaderContainer lightTheme={lightTheme}>
            <Loader type="TailSpin" color="#ff0000" height="60" width="60" />
          </LoaderContainer>
        )
      }}
    </ThemeContext.Consumer>
  )

  render() {
    return (
      <ThemeContext.Consumer>
        {value => {
          const {lightTheme} = value
          return <Header />
        }}
      </ThemeContext.Consumer>
    )
  }
}

export default Trending
