import styled from 'styled-components'

export const BgContainer = styled.div`
  height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background-color: ${props =>
    props.lightTheme === 'true' ? '#f9f9f9' : '#1e293b'};
`

export const Card = styled.div`
  display: flex;
  flex-direction: column;
`
