import styled from 'styled-components'

export const LoaderContainer = styled.div`
  background-color: ${props => (props.lightTheme ? '#f8fafc' : '  #000000')};
  height: 90vh;
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
`

export const SearchContainer = styled.div`
    width: 80%;
    border-radius: 4px;
    display: flex;
    justify-content:space-between;
    align-items: center;
    @media screen and (min-width: 768px){
        width: 60%;
    }
}`

export const SearchIp = styled.input`
    outline: none;
    padding: 5px;
    background-color: transparent;
    width: 90%;
    height: 100%;
  border: 1px solid ${props => (props.lightTheme ? '#64748b' : '#606060')};
  color: ${props => (props.lightTheme ? '#64748b' : '#606060')}
  @media (max-width: 767px) {
    padding: 3px;
    width: 85%;
  }
}`

export const SearchButton = styled.button`
  outline: none;
  padding: 5px;
  cursor: pointer;
  background-color: ${props => (props.lightTheme ? '#ffffff' : '#212121')};
  width: 10%;
  border: 1px solid ${props => (props.lightTheme ? '#64748b' : '#606060')};
  @media (max-width: 767px) {
    padding: 3px;
    width: 15%;
  }
`

export const Cont = styled.div`
  display: flex;
  flex-direction: row;
  width: 100%;
  height: 90.5vh;
`
export const VideosContainer = styled.div`
  width: 100%;
  overflow: auto;
  display: flex;
  flex-direction: column;
  padding: 10px;
  background-color: ${props => (props.lightTheme ? '#f8fafc' : '  #000000')};
`

export const WatchContainer = styled.div`
  width: 100%;
  display: flex;
  justify-content: space-between;
  padding: 20px;
  background-image: url('https://assets.ccbp.in/frontend/react-js/nxt-watch-banner-bg.png');
  background-size: cover;
`
