import {Component} from 'react'
import Cookies from 'js-cookie'
import Loader from 'react-loader-spinner'
import {AiOutlineSearch} from 'react-icons/ai'
import Header from '../Header'
import ThemeContext from '../../context/ThemeContext'
import {
  LoaderContainer,
  SearchContainer,
  SearchIp,
  SearchButton,
} from './styledComponents'

class Home extends Component {
  state = {status: 'loading', searchVal: ''}

  componentDidMount() {
    this.getVideos()
  }

  getVideos = async (searchVal = '') => {
    const jwtToken = Cookies.get('jwt_token')
    const url = `https://apis.ccbp.in/videos/all?search=${searchVal}`
    const options = {
      headers: {
        Authorization: `Bearer ${jwtToken}`,
      },
      method: 'GET',
    }
    const response = await fetch(url, options)
    const data = await response.json()
  }

  renderLoader = () => (
    <LoaderContainer>
      <Loader type="Puff" color="#475569" height="60" width="60" />
    </LoaderContainer>
  )

  renderSearchBar = () => (
    <ThemeContext.Consumer>
      {value => {
        const {lightTheme} = value
        return (
          <SearchContainer>
            <SearchIp
              placeholder="Search Channel"
              type="search"
              value={searchIp}
              onChange={this.onChange}
              onKeyDown={this.onKey}
            />
            <SearchButton onClick={this.onSearch}>
              <AiOutlineSearch size={20} />
            </SearchButton>
          </SearchContainer>
        )
      }}
    </ThemeContext.Consumer>
  )

  renderHome = () => {
    const {status} = this.state
    switch (status) {
      case 'loading':
        return this.renderLoader()

      default:
        return null
    }
  }

  render() {
    return (
      <ThemeContext.Consumer>
        {value => {
          const {lightTheme} = value
          return (
            <>
              <Header lightTheme={lightTheme} />
              {this.renderHome()}
            </>
          )
        }}
      </ThemeContext.Consumer>
    )
  }
}

export default Home
