import ThemeContext from '../../context/ThemeContext'
import {ListImage, ListContainer} from './styledComponents'

const VideoItem = props => {
  const {details} = props
  const {channel} = details
  return (
    <ThemeContext.Consumer>
      {value => {
        const {lightTheme} = value
        return (
          <ListContainer>
            <ListImage src={details.thumbnail_url} alt="thumbnail" />
          </ListContainer>
        )
      }}
    </ThemeContext.Consumer>
  )
}

export default VideoItem
