import {Component} from 'react'
import ThemeContext from '../../context/ThemeContext'
import {
  BgContainer,
  Card,
  WatchLogo,
  FormContainer,
  LabelElement,
  InputElement,
  CheckboxContainer,
  CheckboxLabel,
  LoginButton,
} from './styledComponents'

class LoginRoute extends Component {
  state = {
    showPassword: false,
    username: '',
    password: '',
  }

  onChangeUsername = event => {
    this.setState({username: event.target.value})
  }

  onChangePassword = event => {
    this.setState({password: event.target.value})
  }

  onChangeShowPassword = () => {
    this.setState(prevState => ({showPassword: !prevState.showPassword}))
  }

  render() {
    const {showPassword, username, password} = this.state
    return (
      <ThemeContext.Consumer>
        {value => {
          const {lightTheme} = value
          const LogoUrl = lightTheme
            ? 'https://assets.ccbp.in/frontend/react-js/nxt-watch-logo-light-theme-img.png'
            : 'https://assets.ccbp.in/frontend/react-js/nxt-watch-logo-dark-theme-img.png'
          return (
            <BgContainer lightTheme={`${lightTheme}`}>
              <Card lightTheme={`${lightTheme}`}>
                <WatchLogo src={LogoUrl} alt="nxt watch logo" />
                <FormContainer onSubmit={this.onSubmitForm}>
                  <LabelElement htmlFor="username">USERNAME</LabelElement>
                  <InputElement
                    id="username"
                    type="text"
                    placeholder="Username"
                    onChange={this.onChangeUsername}
                    value={username}
                  />
                  <LabelElement htmlFor="password">PASSWORD</LabelElement>
                  <InputElement
                    id="password"
                    type={showPassword ? 'text' : 'password'}
                    placeholder="Password"
                    onChange={this.onChangePassword}
                    value={password}
                  />
                  <CheckboxContainer>
                    <InputElement
                      id="checkbox"
                      type="checkbox"
                      checked={showPassword}
                      onChange={this.onChangeShowPassword}
                    />
                    <CheckboxLabel htmlFor="checkbox">
                      Show Password
                    </CheckboxLabel>
                  </CheckboxContainer>
                  <LoginButton type="submit">Login</LoginButton>
                </FormContainer>
              </Card>
            </BgContainer>
          )
        }}
      </ThemeContext.Consumer>
    )
  }
}

export default LoginRoute
